list-view-config.query-select:
PREFIX foaf: <http://xmlns.com/foaf/0.1/> PREFIX afn: <http://jena.hpl.hp.com/ARQ/function#> PREFIX bibo: <http://purl.org/ontology/bibo/> PREFIX core: <http://vivoweb.org/ontology/core#> PREFIX owl: <http://www.w3.org/2002/07/owl#> PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>
SELECT DISTINCT ?awardReceipt ?award ?awardLabel ?conferredBy ?conferredByLabel ?dateTimeStart ?dateTimeEnd ?dateTime
WHERE { ?subject ?property ?awardReceipt . ?awardReceipt core:receiptOf ?award . ?award core:receipt ?awardReceipt OPTIONAL { ?award rdfs:label ?awardLabel } OPTIONAL { ?award core:awardConferredBy ?conferredBy . ?conferredBy rdfs:label ?conferredByLabel } OPTIONAL { ?awardReceipt core:dateTimeInterval ?dateTimeInterval OPTIONAL { ?dateTimeInterval core:start ?dateTimeStartValue . ?dateTimeStartValue core:dateTime ?dateTimeStart } } OPTIONAL { ?awardReceipt core:dateTimeInterval ?dateTimeInterval OPTIONAL { ?dateTimeInterval core:end ?dateTimeEndValue . ?dateTimeEndValue core:dateTime ?dateTimeEnd } } OPTIONAL { ?awardReceipt core:dateTimeValue ?dateTimeValue OPTIONAL { ?dateTimeValue core:dateTime ?dateTime
} }
} ORDER BY DESC(?dateTime) DESC(?dateTimeEnd)

list-view-config.query-construct:
PREFIX core: <http://vivoweb.org/ontology/core#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>   
CONSTRUCT { 
    ?subject ?property ?awardReceipt .
    ?awardReceipt ?awardReceiptProperty ?awardReceiptValue .
    ?awardReceipt rdfs:label ?awardReceiptLabel .
    ?awardReceipt core:receiptOf ?award .
    ?award core:receipt ?awardReceipt .
    ?award rdfs:label ?awardLabel
} WHERE {
    {
       ?subject ?property ?awardReceipt 
    } UNION {
       ?subject ?property ?awardReceipt .
       ?awardReceipt ?awardReceiptProperty ?awardReceiptValue 
    } UNION {
        ?subject ?property ?awardReceipt .
        ?awardReceipt ?awardReceiptProperty ?awardReceiptValue .
        ?awardReceipt rdfs:label ?awardReceiptLabel
    } UNION {
        ?subject ?property ?awardReceipt .
        ?awardReceipt ?awardReceiptProperty ?awardReceiptValue .
        ?awardReceipt rdfs:label ?awardReceiptLabel .
        ?awardReceipt core:receiptOf ?award .
        ?award core:receipt ?awardReceipt 
    } UNION {
        ?subject ?property ?awardReceipt .
        ?awardReceipt ?awardReceiptProperty ?awardReceiptValue .
        ?awardReceipt rdfs:label ?awardReceiptLabel .
        ?awardReceipt core:receiptOf ?award .
        ?award core:receipt ?awardReceipt .
        ?award rdfs:label ?awardLabel
    } 
}

    PREFIX bibo: <http://purl.org/ontology/bibo/>
