list-view-config.query-select:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX core: <http://vivoweb.org/ontology/core#> PREFIX afn: <http://jena.hpl.hp.com/ARQ/function#> PREFIX foaf: <http://xmlns.com/foaf/0.1/> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>
SELECT DISTINCT ?subclass ?authorship ?person ?personName WHERE { ?subject ?property ?authorship OPTIONAL { ?authorship core:authorRank ?rank } OPTIONAL { ?authorship core:linkedAuthor ?person . ?person rdfs:label ?personName
OPTIONAL { ?person vitro:mostSpecificType ?subclass . ?subclass rdfs:subClassOf foaf:Person }
}
FILTER ( bound(?person) )
} ORDER BY ?subclass ?rank ?personName

list-view-config.query-construct:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>  
PREFIX foaf:  <http://xmlns.com/foaf/0.1/> 
CONSTRUCT {
    ?subclass rdfs:subClassOf foaf:Person 
} WHERE {
    ?subclass rdfs:subClassOf foaf:Person
}

PREFIX core: <http://vivoweb.org/ontology/core#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> 
PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>   
CONSTRUCT { 
    ?subject ?property ?authorship .
    ?authorship ?authorshipProperty ?authorshipValue .
    ?person rdfs:label ?personName .
    ?person vitro:mostSpecificType ?subclass           
} WHERE {
    {
        ?subject ?property ?authorship 
    } UNION {
        ?subject ?property ?authorship .
        ?authorship ?authorshipProperty ?authorshipValue 
    } UNION {
        ?subject ?property ?authorship .
        ?authorship core:linkedAuthor ?person .                      
        ?person rdfs:label ?personName  
    } UNION {
        ?subject ?property ?authorship .
        ?authorship core:linkedAuthor ?person .                      
        ?person rdfs:label ?personName . 
        ?person vitro:mostSpecificType ?subclass
    }
}
