list-view-config.query-select:
PREFIX afn: <http://jena.hpl.hp.com/ARQ/function#> PREFIX core: <http://vivoweb.org/ontology/core#> PREFIX foaf: <http://xmlns.com/foaf/0.1/> PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>
SELECT ?person ?personName ?positionTitle ?hrJobTitle ?org ?orgName WHERE {
?subject ?property ?person
OPTIONAL { ?person rdfs:label ?personName }
OPTIONAL { ?person core:hrJobTitle ?hrJobTitle }
OPTIONAL { ?person core:personInPosition ?position . ?position rdfs:label ?positionTitle }
OPTIONAL { ?position core:positionInOrganization ?org . ?org rdfs:label ?orgName } } ORDER BY ?personName

list-view-config.query-construct:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>  
PREFIX core: <http://vivoweb.org/ontology/core#>

CONSTRUCT {
    ?subject ?property ?person .
    ?person rdfs:label ?personName .
    ?person core:hrJobTitle ?hrJobTitle 
} WHERE {
    { ?subject ?property ?person  
    } UNION
    { ?subject ?property ?person  .
      ?person rdfs:label ?personName 
    } UNION
    {
      ?subject ?property ?person .
      ?person core:hrJobTitle ?hrJobTitle 
    }
}

PREFIX core: <http://vivoweb.org/ontology/core#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>  
PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>

CONSTRUCT {
    ?subject ?property ?person .
    ?person core:personInPosition ?position .
    ?position rdfs:label ?positionTitle .
    ?position core:positionInOrganization ?org .
    ?org rdfs:label ?orgName .
} WHERE {
    { 
        ?subject ?property ?person .
        ?person core:personInPosition ?position 
    } UNION
    {
        ?subject ?property ?person .
        ?person core:personInPosition ?position .
        ?position rdfs:label ?positionTitle 
    } UNION
    {
        ?subject ?property ?person .
        ?person core:personInPosition ?position .
        ?position core:positionInOrganization ?org 
    }     UNION
    {
        ?subject ?property ?person .
        ?person core:personInPosition ?position .
        ?position core:positionInOrganization ?org .
        ?org rdfs:label ?orgName
    }          
}
